import React, { Component } from "react";
import Like from "./Like";
import TableTitle from "./tableTitle";
class CartTable extends Component {
  handleSort = (sortColumn) => {
    this.setState({ sortColumn });
    console.log(this.state.sortColumn);
  };
  render() {
    const totalPrice = 19;
    if (this.props.cartData.length === 0) {
      return <h1>no items</h1>;
    }
    console.log(this.props.cartData);
    return (
      <React.Fragment>
        <table className="cartTable-table">
          <th className="tableHead">Your cart</th>
          {/* <TableTitle
          column={this.column}
          onSort={this.handleSort}
          sortColumn={this.state.sortColumn}
        /> */}
          <tbody>
            {this.props.cartData.map((data) => (
              <tr className="cartTable-row">
                <td>
                  <img src={data.image} width={80} />
                </td>
                <td>{data.name}</td>
                <td>{data.count}</td>
                <td>{data.price}</td>
                <td className="chech-out-item-button">
                  <button className="chech-out-item-button-color">
                    CHECK OUT
                  </button>
                </td>
                <td>
                  <i
                    class="fas fa-trash"
                    onClick={() => this.props.onDelete(data)}
                  ></i>
                </td>
              </tr>
            ))}
            <tr className="cartTable-final-row">
              <td style={{ paddingLeft: "100px" }}>All items</td>
              <td></td>
              <td>01</td>
              <td>76$</td>
              <td className="chech-out-item-button allItems-Button">
                <button>CHECK OUT ALL</button>
              </td>
            </tr>
          </tbody>
        </table>
        <div></div>
      </React.Fragment>
    );
  }
}

export default CartTable;
