import React from "react";
import Header from "./components/header";
import { Route, Redirect } from "react-router-dom";
import HomePage from "./pages/HomePage";
import Footer from "./components/Footer";
import CartPage from "./pages/CartPage";
import data from "./data/store-items.json";
class App extends React.Component {
  state = {
    cartActivated: false,
    Headerposition: "relative",
  };

  handleHasgChange = (pathname) => {
    console.log(pathname);
    if (pathname == "/cart") {
      this.setState({ cartActivated: true });
    } else if (pathname == "/") {
      this.setState({ Headerposition: "absolute" });
    } else {
      this.setState({ cartActivated: false });
    }
  };
  render() {
    return (
      <React.Fragment>
        <Header activated={this.state.cartActivated} />
        <div>
          <Route
            exact
            path="/"
            render={(props) => (
              <HomePage {...props} onHashChange={this.handleHasgChange} />
            )}
          />
          <Route
            exact
            path="/cart"
            render={(props) => (
              <CartPage {...props} onHashChange={this.handleHasgChange} />
            )}
          />
          {/* <Redirect to="/not-found" exact /> */}
        </div>
        <Footer />
      </React.Fragment>
    );
  }
}

export default App;
